import styled from "styled-components";


const OpenContainer = styled.div`
    display : flex;

    width: 40rem;
    height : 40rem;

    //최상단 위치
    z-index : 999;

    //가운데 위치 시키기
    position : absolute;
    top : 50%;
    left : 50%;
    transform : translate(-50%, -50%);

    border : 1px solid white;

`

const CloseContainer = styled.div`
    display : none
`

//닫기 버튼
const Close = styled.button`
    position : absolute;
    top : 10px;s
    right : 10px;
`

function ModalPage( openModal, setOpenModal ) {

    function closeModal() {
        setOpenModal(false);
    }


    return(
        <OpenContainer>
            ㅎㅇ
            <Close onClick={closeModal}> X </Close>
        </OpenContainer>
    )
}

export default ModalPage;
