import styled from "styled-components";

const Background = styled.div`
    position : fixed;
    top : 0;
    left : 0;
    width : 100vw;
    height : 100vh;
    background-color : rgba(0,0,0,1);
    z-index : 100;
`

const OpenContainer = styled.div`
    width: 40rem;
    height : 40rem;

    //최상단 위치
    z-index : 999;

    //가운데 위치 시키기
    position : absolute;
    top : 50%;
    left : 50%;
    transform : translate(-50%, -50%);

    border : 1px solid white;

    transition : 0.5s;
`


//닫기 버튼
const Close = styled.button`
    position : absolute;
    top : 10px;s
    right : 10px;
    transition : 0.5s;

`

function ModalPage({ setOpenModal }) {

    function closeModal() {
        setOpenModal(false);
    }

    return (
        <Background>
            <OpenContainer>
                <Close onClick={closeModal}> X </Close>
            </OpenContainer>
        </Background>
    )
}

export default ModalPage;
